import Axios from "axios";
import ip from "./host";

const comprobar_usuario = async (Nombre, password) => {
    try{
    let respuesta = await Axios.post(`${ip}login?nombre=${Nombre}&pasword=${password}`);
    console.log(respuesta.data)
    return respuesta;
    }catch{
        console.log("Error...");
        return false;
    }
}

export default comprobar_usuario;
