import Axios from "axios";
import ip from "./host";

const comprobar_usuario = async (Nombre, password) => {
    let respuesta = Axios.post(`${ip}login?nombre=${Nombre}&pasword=${password}`);
    return respuesta ==="OK";
}

export default comprobar_usuario;
